select * from ( 
select  *  from t_dangerous_notice where building_id=#value# 
 )C where rownum=1

SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( select t.building_id,t.building_address,t.builiding_region,t.floor_count,t.house_count ,t.build_area,t.real_type,t.use_desgin,t.building_date,t.build_struct,t.notice_state, t.health_grade_jd,t.is_die,jdinfo_id,WFZZ_STATUS from t_building t where t.health_grade_jd is not null and t.health_grade_jd <> '1' building_address like '%$tBuilding.building_address$%' builiding_region=#tBuilding.builiding_region# real_type=#tBuilding.real_type# use_desgin=#tBuilding.use_desgin# build_struct=#tBuilding.build_struct# health_grade_jd=#tBuilding.health_grade_jd# is_die=#tBuilding.is_die#
$_sortname$ $_sortorder$
) A WHERE ROWNUM <= #_page_end# ) WHERE RN > #_page_start#

SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( select * from t_appraisal_task where info_state <> '9' (building_address like '%$tAppraisalTask.building_address$%' or building_newaddress like '%$tAppraisalTask.building_address$%') building_region=#tAppraisalTask.building_region# safe_grade=#tAppraisalTask.safe_grade# building_date=#tAppraisalTask.building_date# agent like '%$tAppraisalTask.agent$%' data_origin=#tAppraisalTask.data_origin#
$_sortname$ $_sortorder$
) A WHERE ROWNUM <= #_page_end# ) WHERE RN > #_page_start#
